mapper.insert:
INSERT INTO tb_board (
	
) VALUES (
	  #{idx}
	, #{title}
	, #{content}
	, #{writer}
	, 'Y'
	, IFNULL(#{noticeYn}, 'N')
	, IFNULL(#{secretYn}, 'N')
	, 0
	, NOW()
	, NULL
)

mapper.update:
UPDATE tb_board
SET
	  title = #{title}
	, content = #{content}
	, writer = #{writer}
	, notice_yn = #{noticeYn}
	, secret_yn = #{secretYn}
	, update_time = NOW()
WHERE
	idx = #{idx}
