mapper.insert:
INSERT INTO tb_comment (

) VALUES (
	  #{idx}
	, #{boardIdx}
	, #{content}
	, #{writer}
	, IFNULL(#{secretYn}, 'N')
	, IFNULL(#{useYn}, 'Y')
	, NOW()
	, NULL
)

mapper.update:
UPDATE tb_comment
SET
	  update_time = NOW()
	, content = #{content}
	, writer = #{writer}
	, secret_yn = #{secretYn}
WHERE
	idx = #{idx}
AND
	board_idx = #{boardIdx}
